import React, { useState } from "react";
import { Link } from "react-router-dom";

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  return (
    <nav className="navbar">
      <style>
        {`
          .navbar {
            background-color: #fff;
            box-shadow: 0 2px 20px rgba(0,0,0,0.1);
            padding: 1rem 0;
            position: fixed;
            width: 100%;
            top: 0;
            z-index: 1000;
            backdrop-filter: blur(10px);
          }
          
          .nav-container {
            max-width: 1200px;
            margin: 0 auto;
            display: flex;
            justify-content: space-between;
            align-items: center;
            padding: 0 2rem;
          }
          
          .nav-logo {
            text-decoration: none;
            color: #2c3e50;
            font-size: 1.5rem;
            font-weight: 700;
          }
          
          .nav-menu {
            display: flex;
            gap: 2rem;
          }
          
          .nav-link {
            text-decoration: none;
            color: #333;
            font-weight: 500;
            transition: color 0.3s ease;
            position: relative;
          }
          
          .nav-link:hover {
            color: #667eea;
          }
          
          .nav-link::after {
            content: '';
            position: absolute;
            width: 0;
            height: 2px;
            bottom: -5px;
            left: 0;
            background-color: #667eea;
            transition: width 0.3s ease;
          }
          
          .nav-link:hover::after {
            width: 100%;
          }
          
          .nav-toggle {
            display: none;
            flex-direction: column;
            cursor: pointer;
          }
          
          .bar {
            width: 25px;
            height: 3px;
            background-color: #333;
            margin: 3px 0;
            transition: 0.3s;
          }
          
          @media (max-width: 768px) {
            .nav-menu {
              position: fixed;
              left: -100%;
              top: 70px;
              flex-direction: column;
              background-color: #fff;
              width: 100%;
              text-align: center;
              transition: 0.3s;
              box-shadow: 0 10px 27px rgba(0,0,0,0.05);
              padding: 2rem 0;
            }
          
            .nav-menu.active {
              left: 0;
            }
          
            .nav-toggle {
              display: flex;
            }
          }
        `}
      </style>

      <div className="nav-container">
        <Link to="/" className="nav-logo">
          <h2>ðŸ’Ž Luxury Jewelry Auctions</h2>
        </Link>

        <div className={`nav-menu ${isMenuOpen ? "active" : ""}`}>
          <Link
            to="/"
            className="nav-link"
            onClick={() => setIsMenuOpen(false)}
          >
            Home
          </Link>
          <Link
            to="/jewelry"
            className="nav-link"
            onClick={() => setIsMenuOpen(false)}
          >
            Jewelry Items
          </Link>
          <Link
            to="/about"
            className="nav-link"
            onClick={() => setIsMenuOpen(false)}
          >
            About
          </Link>
          <Link
            to="/contact"
            className="nav-link"
            onClick={() => setIsMenuOpen(false)}
          >
            Contact
          </Link>
        </div>

        <div className="nav-toggle" onClick={() => setIsMenuOpen(!isMenuOpen)}>
          <span className="bar"></span>
          <span className="bar"></span>
          <span className="bar"></span>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
