import React from "react";
import { Link } from "react-router-dom";

const JewelryCard = ({ item }) => {
  return (
    <div className="jewelry-card">
      <style>
        {`
          .jewelry-card {
            background: white;
            border-radius: 12px;
            overflow: hidden;
            box-shadow: 0 4px 20px rgba(0,0,0,0.1);
            transition: transform 0.3s ease, box-shadow 0.3s ease;
          }
          
          .jewelry-card:hover {
            transform: translateY(-5px);
            box-shadow: 0 10px 40px rgba(0,0,0,0.15);
          }
          
          .card-image {
            position: relative;
            height: 250px;
            overflow: hidden;
          }
          
          .card-image img {
            width: 100%;
            height: 100%;
            object-fit: cover;
            transition: transform 0.3s ease;
          }
          
          .jewelry-card:hover .card-image img {
            transform: scale(1.05);
          }
          
          .card-overlay {
            position: absolute;
            top: 1rem;
            left: 1rem;
            right: 1rem;
            display: flex;
            justify-content: space-between;
          }
          
          .card-overlay span {
            padding: 0.25rem 0.75rem;
            border-radius: 20px;
            font-size: 0.8rem;
            font-weight: 600;
          }
          
          .item-condition {
            background: rgba(255,255,255,0.9);
            color: #27ae60;
          }
          
          .item-metal {
            background: rgba(255,215,0,0.9);
            color: #000;
          }
          
          .card-content {
            padding: 1.5rem;
          }
          
          .item-name {
            font-size: 1.2rem;
            font-weight: 600;
            margin-bottom: 0.5rem;
            color: #2c3e50;
          }
          
          .item-description {
            color: #666;
            margin-bottom: 1rem;
            line-height: 1.5;
          }
          
          .item-details {
            margin-bottom: 1rem;
          }
          
          .gemstone-tag {
            background: linear-gradient(45deg, #667eea, #764ba2);
            color: white;
            padding: 0.25rem 0.75rem;
            border-radius: 20px;
            font-size: 0.8rem;
            font-weight: 500;
          }
          
          .card-footer {
            display: flex;
            justify-content: space-between;
            align-items: center;
          }
          
          .item-price {
            font-size: 1.3rem;
            font-weight: 700;
            color: #e74c3c;
          }
          
          .view-details-btn {
            color: #667eea;
            text-decoration: none;
            font-weight: 600;
            transition: color 0.3s ease;
          }
          
          .view-details-btn:hover {
            color: #764ba2;
          }
          
          @media (max-width: 768px) {
            .card-footer {
              flex-direction: column;
              gap: 1rem;
              align-items: flex-start;
            }
            
            .view-details-btn {
              align-self: flex-end;
            }
          }
        `}
      </style>

      <div className="card-image">
        <img
          src={item.image}
          alt={item.name}
          onError={(e) => {
            e.target.src =
              "https://via.placeholder.com/400x300?text=Jewelry+Image";
          }}
        />
        <div className="card-overlay">
          <span className="item-condition">{item.condition}</span>
          <span className="item-metal">{item.metalType}</span>
        </div>
      </div>

      <div className="card-content">
        <h3 className="item-name">{item.name}</h3>
        <p className="item-description">{item.description}</p>

        <div className="item-details">
          <span className="gemstone-tag">ðŸ’Ž {item.gemstone}</span>
        </div>

        <div className="card-footer">
          <div className="item-price">${item.price.toLocaleString()}</div>
          <Link to={`/jewelry/${item.id}`} className="view-details-btn">
            View Details â†’
          </Link>
        </div>
      </div>
    </div>
  );
};

export default JewelryCard;
