// components/JewelryCard.jsx
import React from "react";
import { Link } from "react-router-dom";

const JewelryCard = ({ item }) => {
  return (
    <div className="jewelry-card">
      <style>
        {`
          .jewelry-card {
            border: 1px solid #ddd;
            border-radius: 8px;
            overflow: hidden;
            transition: transform 0.3s ease, box-shadow 0.3s ease;
            background: white;
          }
          
          .jewelry-card:hover {
            transform: translateY(-5px);
            box-shadow: 0 10px 25px rgba(0,0,0,0.1);
          }
          
          .jewelry-image {
            width: 100%;
            height: 250px;
            object-fit: cover;
          }
          
          .jewelry-info {
            padding: 1.5rem;
          }
          
          .jewelry-name {
            font-size: 1.2rem;
            font-weight: 600;
            margin-bottom: 0.5rem;
            color: #333;
          }
          
          .jewelry-price {
            font-size: 1.1rem;
            color: #007bff;
            font-weight: 600;
            margin-bottom: 1rem;
          }
          
          .view-details-btn {
            width: 100%;
            padding: 0.75rem;
            background-color: #007bff;
            color: white;
            border: none;
            border-radius: 5px;
            cursor: pointer;
            transition: background-color 0.3s ease;
            text-decoration: none;
            display: inline-block;
            text-align: center;
          }
          
          .view-details-btn:hover {
            background-color: #0056b3;
          }
        `}
      </style>

      <img
        src={item.image}
        alt={item.name}
        className="jewelry-image"
        onError={(e) => {
          e.target.src =
            "https://via.placeholder.com/400x300?text=Jewelry+Image";
        }}
      />
      <div className="jewelry-info">
        <h3 className="jewelry-name">{item.name}</h3>
        <p className="jewelry-price">${item.price.toLocaleString()}</p>
        <Link to={`/jewelry/${item.id}`} className="view-details-btn">
          View Details
        </Link>
      </div>
    </div>
  );
};

export default JewelryCard;
