// components/JewelryDetails.jsx
import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";

const JewelryDetails = () => {
  const { id } = useParams();
  const [item, setItem] = useState(null);
  const [bidAmount, setBidAmount] = useState("");

  useEffect(() => {
    // Mock data - in real app, fetch from API
    const mockItems = [
      {
        id: 1,
        name: "Diamond Engagement Ring",
        description:
          "Beautiful platinum ring featuring a brilliant 1-carat diamond in a classic solitaire setting. The diamond is VS1 clarity and F color, making it an excellent choice for an engagement ring.",
        price: 2500,
        image:
          "https://images.unsplash.com/photo-1605100804763-247f67b3557e?w=600",
        metalType: "Platinum",
        gemstone: "Diamond",
        condition: "Excellent",
        weight: "3.2g",
        dimensions: "6mm x 6mm",
      },
      {
        id: 2,
        name: "Sapphire Bracelet",
        description:
          "Elegant 18k gold bracelet adorned with natural blue sapphires. The bracelet features a secure clasp and is adjustable for perfect fit.",
        price: 1800,
        image:
          "https://images.unsplash.com/photo-1515562141207-7a88fb7ad5e5?w=600",
        metalType: "Gold",
        gemstone: "Sapphire",
        condition: "Good",
        weight: "15.5g",
        dimensions: "7 inches",
      },
      {
        id: 3,
        name: "Pearl Necklace",
        description:
          "Classic cultured pearl necklace with sterling silver clasp. Features perfectly matched pearls with excellent luster.",
        price: 950,
        image:
          "https://images.unsplash.com/photo-1596944948024-92c9bad2b0c3?w=600",
        metalType: "Silver",
        gemstone: "Pearl",
        condition: "Very Good",
        weight: "22g",
        dimensions: "18 inches",
      },
    ];

    const foundItem = mockItems.find((i) => i.id === parseInt(id));
    setItem(foundItem);
  }, [id]);

  const handleBid = (e) => {
    e.preventDefault();
    if (bidAmount > item.price) {
      alert(`Bid placed successfully: $${bidAmount}`);
      // In real app, this would update the item's price and bid history
    } else {
      alert("Bid must be higher than current price");
    }
  };

  if (!item) {
    return <div>Loading...</div>;
  }

  return (
    <div className="jewelry-details">
      <style>
        {`
          .jewelry-details {
            max-width: 1000px;
            margin: 0 auto;
            padding: 2rem;
          }
          
          .back-button {
            display: inline-block;
            margin-bottom: 2rem;
            color: #007bff;
            text-decoration: none;
          }
          
          .details-container {
            display: grid;
            grid-template-columns: 1fr 1fr;
            gap: 3rem;
          }
          
          .jewelry-image-large {
            width: 100%;
            border-radius: 8px;
            box-shadow: 0 4px 15px rgba(0,0,0,0.1);
          }
          
          .details-info h1 {
            margin-bottom: 1rem;
            color: #333;
          }
          
          .price {
            font-size: 1.5rem;
            color: #007bff;
            font-weight: 600;
            margin: 1rem 0;
          }
          
          .description {
            margin: 1.5rem 0;
            line-height: 1.6;
            color: #666;
          }
          
          .specs {
            margin: 1.5rem 0;
          }
          
          .spec-item {
            display: flex;
            justify-content: space-between;
            padding: 0.5rem 0;
            border-bottom: 1px solid #eee;
          }
          
          .bid-form {
            margin-top: 2rem;
            padding: 1.5rem;
            background: #f8f9fa;
            border-radius: 8px;
          }
          
          .bid-input {
            width: 100%;
            padding: 0.75rem;
            margin: 0.5rem 0;
            border: 1px solid #ddd;
            border-radius: 4px;
          }
          
          .bid-button {
            width: 100%;
            padding: 0.75rem;
            background-color: #28a745;
            color: white;
            border: none;
            border-radius: 4px;
            cursor: pointer;
            margin-top: 1rem;
          }
          
          .bid-button:hover {
            background-color: #218838;
          }
          
          @media (max-width: 768px) {
            .details-container {
              grid-template-columns: 1fr;
            }
          }
        `}
      </style>

      <Link to="/jewelry" className="back-button">
        ‚Üê Back to Jewelry
      </Link>

      <div className="details-container">
        <div>
          <img
            src={item.image}
            alt={item.name}
            className="jewelry-image-large"
            onError={(e) => {
              e.target.src =
                "https://via.placeholder.com/600x500?text=Jewelry+Image";
            }}
          />
        </div>

        <div className="details-info">
          <h1>{item.name}</h1>
          <div className="price">${item.price.toLocaleString()}</div>

          <p className="description">{item.description}</p>

          <div className="specs">
            <h3>Specifications</h3>
            <div className="spec-item">
              <span>Metal Type:</span>
              <span>{item.metalType}</span>
            </div>
            <div className="spec-item">
              <span>Gemstone:</span>
              <span>{item.gemstone}</span>
            </div>
            <div className="spec-item">
              <span>Condition:</span>
              <span>{item.condition}</span>
            </div>
            <div className="spec-item">
              <span>Weight:</span>
              <span>{item.weight}</span>
            </div>
            <div className="spec-item">
              <span>Dimensions:</span>
              <span>{item.dimensions}</span>
            </div>
          </div>

          <div className="bid-form">
            <h3>Place a Bid</h3>
            <form onSubmit={handleBid}>
              <input
                type="number"
                className="bid-input"
                placeholder={`Minimum bid: $${item.price + 100}`}
                value={bidAmount}
                onChange={(e) => setBidAmount(e.target.value)}
                min={item.price + 100}
                required
              />
              <button type="submit" className="bid-button">
                Place Bid
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default JewelryDetails;
