// components/JewelryList.jsx
import React, { useState, useEffect } from "react";
import JewelryCard from "./JewelryCard";
import JewelryFilters from "./JewelryFilters";
import JewelrySearch from "./JewelrySearch";

const JewelryList = () => {
  const [jewelryItems, setJewelryItems] = useState([]);
  const [filteredItems, setFilteredItems] = useState([]);
  const [filters, setFilters] = useState({
    metalType: "",
    gemstone: "",
    minPrice: 0,
    maxPrice: 10000,
    condition: "",
  });

  useEffect(() => {
    // Mock data - in real app, this would come from an API
    const mockJewelry = [
      {
        id: 1,
        name: "Diamond Engagement Ring",
        description: "Beautiful platinum ring with 1ct diamond",
        price: 2500,
        image:
          "https://images.unsplash.com/photo-1605100804763-247f67b3557e?w=400",
        metalType: "Platinum",
        gemstone: "Diamond",
        condition: "Excellent",
      },
      {
        id: 2,
        name: "Sapphire Bracelet",
        description: "Elegant gold bracelet with blue sapphires",
        price: 1800,
        image:
          "https://images.unsplash.com/photo-1515562141207-7a88fb7ad5e5?w=400",
        metalType: "Gold",
        gemstone: "Sapphire",
        condition: "Good",
      },
      {
        id: 3,
        name: "Pearl Necklace",
        description: "Classic pearl necklace with silver clasp",
        price: 950,
        image:
          "https://images.unsplash.com/photo-1596944948024-92c9bad2b0c3?w=400",
        metalType: "Silver",
        gemstone: "Pearl",
        condition: "Very Good",
      },
    ];
    setJewelryItems(mockJewelry);
    setFilteredItems(mockJewelry);
  }, []);

  const handleSearch = (searchTerm) => {
    const filtered = jewelryItems.filter((item) =>
      item.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredItems(filtered);
  };

  const handleFilterChange = (newFilters) => {
    setFilters(newFilters);

    const filtered = jewelryItems.filter((item) => {
      return (
        (newFilters.metalType === "" ||
          item.metalType === newFilters.metalType) &&
        (newFilters.gemstone === "" || item.gemstone === newFilters.gemstone) &&
        item.price >= newFilters.minPrice &&
        item.price <= newFilters.maxPrice &&
        (newFilters.condition === "" || item.condition === newFilters.condition)
      );
    });

    setFilteredItems(filtered);
  };

  return (
    <div>
      <style>
        {`
          .jewelry-list-container {
            padding: 2rem;
            max-width: 1200px;
            margin: 0 auto;
          }
          
          .filters-search-container {
            display: grid;
            grid-template-columns: 300px 1fr;
            gap: 2rem;
            margin-bottom: 2rem;
          }
          
          .jewelry-grid {
            display: grid;
            grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
            gap: 2rem;
            margin-top: 2rem;
          }
          
          .no-items {
            text-align: center;
            padding: 2rem;
            color: #666;
          }
          
          @media (max-width: 768px) {
            .filters-search-container {
              grid-template-columns: 1fr;
            }
            
            .jewelry-grid {
              grid-template-columns: 1fr;
            }
          }
        `}
      </style>

      <div className="jewelry-list-container">
        <h1>Jewelry Collection</h1>

        <div className="filters-search-container">
          <JewelryFilters
            filters={filters}
            onFilterChange={handleFilterChange}
          />
          <JewelrySearch onSearch={handleSearch} />
        </div>

        <div className="jewelry-grid">
          {filteredItems.length > 0 ? (
            filteredItems.map((item) => (
              <JewelryCard key={item.id} item={item} />
            ))
          ) : (
            <div className="no-items">
              <p>No jewelry items match your criteria.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default JewelryList;
